import { Component, style } from '@angular/core';
import { IonicPage, NavController, NavParams, Platform } from 'ionic-angular';
import * as _ from "lodash";
import { DispoProvider } from '../../providers/dispo/dispo';
import { AlertController } from 'ionic-angular';
import { CropPage } from "../crop/crop";
//PDF
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts';
pdfMake.vfs = pdfFonts.pdfMake.vfs;
import { File } from '@ionic-native/file';
import { FileOpener } from '@ionic-native/file-opener';



@IonicPage()
@Component({
  selector: 'page-presentacion',
  templateUrl: 'presentacion.html',
})
export class PresentacionPage {
  pdfObj = null;

  constructor(private file: File, private fileOpener: FileOpener, private plt: Platform, public navCtrl: NavController, public provide: DispoProvider, public alertCtrl: AlertController) {

  }

  ionViewDidLoad() {
    //console.log(this.datos);

  }
  /*
  +------------------------------------------------+
  |   BOTONES
  +------------------------------------------------+
  */
  clckQuitar(item) {
    this.provide.quitarItemPresentacion(item);
  }

  clckRecortar(item) {
    console.log(item);/* 
    this.navCtrl.push(CropPage, {
      tvalla: item,
    }); */
  }

  clckPdf() {
    this.createPDF();
    this.descargarPDF();
  }

  /*
  +------------------------------------------------+
  |   PDF
  +------------------------------------------------+
  */

  descargarPDF() {
    if (this.plt.is('cordova')) {
      this.pdfObj.getBuffer((buffer) => {
        var blob = new Blob([buffer], { type: 'application/pdf' });

        // Save the PDF to the data Directory of our App
        this.file.writeFile(this.file.externalDataDirectory, 'myletter.pdf', blob, { replace: true }).then(fileEntry => {
          // Open the PDf with the correct OS tools
          this.fileOpener.open(this.file.externalDataDirectory + 'myletter.pdf', 'application/pdf');
        })
      });
    } else {
      // On a browser simply use download!
      this.pdfObj.download();
    }
  }
  cuerpoPDF() {
    var content = [];

    var caratula={
      text: "\nDISPONIBILIDAD ACTUAL DE PASACALLES Y PASARELAS",
      style: 'title',
      alignment: 'center',
      pageBreak: 'after'
    } 
    content.push(caratula);

    var array = this.provide.getItemsPresentacion();
    for (let index = 0; index < array.length; index++) {
      const element = array[index];
      //console.log(element.region);
      var texto = {
        text: "\n\nRuta: "+element.region,
        style: 'title',
        alignment: 'center',
        pageBreak: 'after'
      }
      content.push(texto);

      for (let index2 = 0; index2 < element.vallas.length; index2++) {
        const element2 = element.vallas[index2];
        var tx = {
          text:"."
        }
        content.push(tx);
        var subText = {
          text: element2.ubicacion,
          style: 'header'
        }
        content.push(subText);
        
        var medidas = {
          text: element2.alto + " x " + element2.ancho + " mts",
          style:'subheader'
        }

        content.push(medidas);

        var imagen = {
          image: element2.url2,
          width: 800,
          alignment: 'center'
        }
        content.push(imagen);

        //console.log(element2.url2);
      }
    }
    return content;
  }

  estilosPDF() {

  }

  createPDF() {
    var docDefinition = {
      content: this.cuerpoPDF(),
      styles: {
        title:{
          fontSize: 65,
          bold: true,
        },
        header: {
          fontSize: 28,
          bold: true,
        },
        subheader: {
          fontSize: 20,
          bold: true,
          //smargin: [0, 15, 0, 0]
        },
        story: {
          italic: true,
          alignment: 'center',
          width: '50%',
        },
      },
      footer: {
         image: "data:image/png;base64,"
        , height: 70,
        width: 800,
        alignment: 'center',
      },
      pageSize: 'LETTER',
      pageOrientation: 'landscape',
      pageMargins: [40, 20, 40, 70],
    }
    this.pdfObj = pdfMake.createPdf(docDefinition);
  }


  

 
}
